update ec_records set expiry_date=TO_DATE('{{variables.updateECReportExpiryDate}}', 'YYYY-MM-DD'), environmental_password=pgp_sym_encrypt('{{variables.updateECReportEnvironmentalPassword}}', '%%client.GEOPANAP_ENCRYPTION_KEY%%', 'compress-algo=1, cipher-algo=aes256'),status='{{variables.updateECReportStatus}}', presentation_date=TO_DATE('{{variables.updateECReportPresentationDate}}', 'YYYY-MM-DD'),updated_at=current_timestamp where id={{variables.updateECReportId}};